protected override void Test(Stream target)
{
    var model = new SheetWithCellLabels
    {
        Cell1 = Guid.NewGuid().ToString(),
        Cell2 = Guid.NewGuid().ToString()
    };

    XlsxSerializer.Instance.Serialize(model, target);

    var deserialized = XlsxSerializer.Instance.Deserialize&lt;SheetWithCellLabels&gt;(target);

    Assert.Equal(model.Cell1, deserialized.Cell1);
    Assert.Equal(model.Cell2, deserialized.Cell2);
}

//Sets default label style for the sheet
[LabelStyle(FontStyle = FontStyle.Italic, BackgroundColor = "Silver")]
public class SheetWithCellLabels
{
    [XlsCell("B1")]
    [Label("First Label")]
    //Overwrites label style for the property
    [LabelStyle(HorizontalAlignment = ExcelHorizontalAlignment.Right, FontStyle = FontStyle.Strike, BackgroundColor = "#0000ff", Color = "White")]
    public string Cell1 { get; set; }

    [XlsCell("C4")]
    //Sets label style for this property
    [LabelStyle()]
    public string Cell2 { get; set; }
}
