public class CollectionItemWithFormula
{
    [XlsColumn("A")]
    public int Value1 { get; set; }

    [XlsColumn("B")]
    public int Value2 { get; set; }

    [XlsColumn("C")]
    [R1C1Formula("R[0]C[-2] + R[0]C[-1]")]
    public int RowSum { get; set; }
}

protected override void Test(Stream target)
{
    var model = new SheetWithFormulas
    {
        Value1 = 1,
        Value2 = 2
    };

    for (var i = 0; i &lt; 10; i++)
    {
        model.Items.Add(new CollectionItemWithFormula
        {
            Value1 = i,
            Value2 = i * 2
        });
    }

    XlsxSerializer.Instance.Serialize(model, target);

    // There is nothing to assert, formulas are calculated when the workbook is opened in Excel
}

public class SheetWithFormulas
{
    [XlsCell("A1")]
    public int Value1 { get; set; }

    [XlsCell("B1")]
    public int Value2 { get; set; }

    [XlsCell("C1")]
    [Formula("A1+A2")]
    public int Value3 { get; set; }

    [XlsCell("A3")]
    public List&lt;CollectionItemWithFormula&gt; Items { get; } = new List&lt;CollectionItemWithFormula&gt;();
}
